import img12 from "../assets/images/visual arts/65d40626-ca68-4030-9345-3ebd41facfa3.png";
import img13 from "../assets/images/visual arts/a9a3a4b2-90b3-46a9-9faa-04dd8dd15aae.png";
import img14 from "../assets/images/visual arts/4473c63b-2aee-4123-b72a-cb7451566701.png";
import img15 from "../assets/images/visual arts/bd54df82-d593-4aa8-ad39-148b41dc39df.png";

import Footer from "../components/Footer.js";

import FadeIn from 'react-fade-in';
import Error from "../components/Error.js";


import '../assets/css/visual-arts.css';


const viewportContext = React.createContext({});

const ViewportProvider = ({ children }) => {
  const [width, setWidth] = React.useState(window.innerWidth);
  const [height, setHeight] = React.useState(window.innerHeight);
  const handleWindowResize = () => {
    setWidth(window.innerWidth);
    setHeight(window.innerHeight);
  };

  React.useEffect(() => {
    window.addEventListener("resize", handleWindowResize);
    return () => window.removeEventListener("resize", handleWindowResize);
  }, []);

  return (
    <viewportContext.Provider value={{ width, height }}>
      {children}
    </viewportContext.Provider>
  );
};

const useViewport = () => {
  const { width, height } = React.useContext(viewportContext);
  return { width, height };
};

const MobileComponent = () =>(
    <>
    <Error />
    </>);


const DesktopComponent = () => (
<>
    <div id = "everything">
    <FadeIn>

        <div>
            <h1 class = "visual-arts">VISUAL ARTS</h1>
        </div>

        <section id = "center">
            <div class = "images">
                <img class = "row1" src = {img1}/>
                <img class = "row1" src = {img2}/>
                <img class = "row1" src = {img3}/>



                <img class = "row2" src = {img4}/>
                <img class = "row2" src = {img5}/>    

                <img class = "row3" src = {img6}/>
                <img class = "row3" src = {img7}/>    

                <img class = "row4" src = {img8}/>
                <img class = "row4" src = {img9}/>    

                <img class = "row5" src = {img10}/>
                <img class = "row5" src = {img11}/>
                <img class = "row5" src = {img12}/>    

                <img class = "row6" src = {img13}/>
                <img class = "row6" src = {img14}/>
                <img class = "row6" src = {img15}/>

                </div>
            </section>

            </FadeIn>

        <div id = "footart">
            <Footer/> 
        </div>
        </div>

    </>
);

const MyComponent = () => {
    const { width } = useViewport();
    const breakpoint = 750;

    return width < breakpoint ? <MobileComponent /> : <DesktopComponent />;
  };

  export default function VisualArts() {
    return (
      <ViewportProvider>
        <MyComponent />
      </ViewportProvider>
    );
  }
